mapper.select:
SELECT poi_id, name, description, address, latitude, longitude, thumbnail_url, region_id
FROM POI


        region_id = #{regionId}

mapper.update:
UPDATE POI
SET description = #{description}
WHERE poi_id = #{poiId}

mapper.select:
SELECT *
FROM POI
WHERE latitude BETWEEN #{minLat} AND #{maxLat}
AND longitude BETWEEN #{minLng} AND #{maxLng}

    AND content_type_id = #{contentTypeId}

LIMIT 100
