mapper.select:

  SELECT user_id, email, password, nickname, role, created_at 
  FROM Users 
  WHERE email = #{email}

mapper.update:
  
UPDATE Users
SET nickname = #{nickname},
    password = #{password}
WHERE user_id = #{userId}


  UPDATE Users
  SET password = #{password}
  WHERE user_id = #{userId}

                   
UPDATE Users
SET role = #{role}
WHERE user_id = #{userId}
